INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
	(1, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Sambungkan ke WooCommerce.com', 'Sambungkan untuk mendapatkan pemberitahuan dan pembaruan produk penting.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:20:00', NULL, 0, 'plain', '', 0, 'info'),
	(2, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, pembaruan produk, dan inspirasi', 'Kami di sini untuk Anda - dapatkan tips, pembaruan produk, dan inspirasi di kotak email Anda', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:20:16', NULL, 0, 'plain', '', 0, 'info'),
	(3, 'wc-admin-marketing-intro', 'info', 'en_US', 'Terhubung dengan audiens Anda', 'Kembangkan basis pelanggan dan tingkatkan penjualan dengan alat pemasaran yang dibuat untuk WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:20:17', NULL, 0, 'plain', '', 0, 'info'),
	(4, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'Pelajari lebih lanjut mengenai Pengaturan Produk', 'Di video ini, Anda akan menemukan informasi mengenai cara mengonfigurasi pengaturan produk, seperti tampilan produk, mengedit pilihan persediaan, dan masih banyak lagi.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-08 03:20:07', NULL, 0, 'plain', '', 0, 'info'),
	(5, 'wc-admin-orders-milestone', 'info', 'en_US', 'Pesanan pertama diterima', 'Selamat atas pesanan pertama Anda! Sekarang adalah waktu yang tepat untuk mempelajari cara mengelola pesanan Anda.', '{}', 'actioned', 'woocommerce-admin', '2020-09-08 05:20:02', NULL, 0, 'plain', '', 0, 'info'),
	(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Instal aplikasi seluler Woo', 'Instal aplikasi seluler WooCommerce untuk mengelola pesanan, menerima pemberitahuan penjualan, dan melihat metrik kunci — di mana pun Anda berada.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-12 03:21:34', NULL, 0, 'plain', '', 0, 'info'),
	(7, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Bantu WooCommerce menjadi lebih baik dengan pelacakan penggunaan', 'Mengumpulkan data penggunaan memungkinkan kami meningkatkan WooCommerce. Toko Anda akan dipertimbangkan selagi kami mengevaluasi fitur baru, menilai kualitas pembaruan, atau menentukan apakah sebuah pembaruan masuk akal. Anda selalu dapat mengunjungi <a href="http://localhost/ecommerce/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com" target="_blank">Pengaturan</a> dan memilih untuk berhenti berbagi data. <a href="https://woocommerce.com/usage-tracking" target="_blank">Baca selengkapnya</a> tentang data yang kami kumpulkan.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-14 23:21:04', NULL, 0, 'plain', '', 0, 'info'),
	(8, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Berikan masukan', 'Setelah Anda memilih kami sebagai mitra, tujuan kami adalah memastikan bahwa kami memberikan alat yang tepat untuk memenuhi kebutuhan Anda. Kami menantikan masukan Anda mengenai pengalaman penyiapan toko, sehingga kami dapat memperbaikinya di masa mendatang.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-17 08:58:42', NULL, 0, 'plain', '', 0, 'info'),
	(9, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Tahukah Anda?', 'Toko berbasis WooCommerce membutuhkan rata-rata 31 hari untuk mendapatkan penjualan pertama. Pilihan Anda sudah tepat! Apakah menurut Anda tipe wawasan ini bermanfaat?', '{}', 'unactioned', 'woocommerce-admin', '2020-09-17 08:58:42', NULL, 0, 'plain', '', 0, 'info');
